export class ReportsComponent {
  constructor(reportService) {
    this.reportService = reportService;
    this.reports = [];
    this.loading = false;
    this.error = null;
    this.selectedMonth = new Date().getMonth() + 1; // ברירת מחדל - החודש הנוכחי

    this.selectedCourseId = '';
    this.selectedEmployeeId = '';
  }

  getReportsByMonth() {
    if (!this.selectedMonth || this.selectedMonth < 1 || this.selectedMonth > 12) {
      this.error = 'אנא הכנס חודש תקין בין 1 ל-12';
      return;
    }

    this.loading = true;
    this.error = null;
    this.reportService.getReportsByMonth(this.selectedMonth).subscribe({
      next: data => {
        this.reports = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'שגיאה בטעינת הדוחות לפי חודש';
        this.loading = false;
      }
    });
  }

  getReportsByCourse() {
    if (!this.selectedCourseId) {
      this.error = 'אנא הכנס קוד קורס';
      return;
    }

    this.loading = true;
    this.error = null;
    this.reportService.getReportsByCourse(this.selectedCourseId).subscribe({
      next: data => {
        this.reports = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'שגיאה בטעינת הדוחות לפי קורס';
        this.loading = false;
      }
    });
  }

  getReportsByEmployee() {
    if (!this.selectedEmployeeId) {
      this.error = 'אנא הכנס תעודת זהות של עובד';
      return;
    }

    this.loading = true;
    this.error = null;
    this.reportService.getReportsByEmployee(this.selectedEmployeeId).subscribe({
      next: data => {
        this.reports = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'שגיאה בטעינת הדוחות לפי עובד';
        this.loading = false;
      }
    });
  }

}
